    internet: 'Fiber Internet',
    description: 'A spacious loft apartment with balcony and smart home features.',
    image: 'https://cf.bstatic.com/xdata/images/hotel/max1024x768/524964658.jpg?k=c6f3073a77b8c22957c71c50160dd9526afcb6bccdf96779b0082824bf0e345d&o=&hp=1'
  },
  {
    title: 'One Bedroom Unit',
    location: 'Embakasi, Nairobi',
    lat: -1.322, lon: 36.925,
    price: 10000,
    beds: 1,
    baths: 1,
    internet: 'Basic Wi-Fi',
    description: 'Affordable one-bedroom home near the airport.',
    image: 'data:image/jpeg;base64,'
  },
  {
    title: 'Three Bedroom House',
    location: 'Runda, Nairobi',
    lat: -1.217, lon: 36.826,
    price: 45000,
    beds: 3,
    baths: 2,
    internet: 'Premium Internet',
    description: 'Family-sized house in a gated estate with private parking.',
    image: 'https://images.unsplash.com/photo-1580587771525-78b9dba3b914'
  },
  {
    title: 'Serviced Apartment',
    location: 'Lavington, Nairobi',
    lat: -1.293, lon: 36.753,
    price: 35000,
    beds: 2,
    baths: 2,
    internet: 'Wi-Fi + Smart TV',
    description: 'Fully serviced apartment ideal for short- and long-term stays.',
    image: 'data:image/jpeg;base64,'
  }
]

const filteredProperties = computed(() => {
  return properties.filter(p => {
    const locMatch = !filters.value.location || p.location.toLowerCase().includes(filters.value.location)
    return (
      locMatch &&
      (!filters.value.minPrice || p.price >= filters.value.minPrice) &&
      (!filters.value.maxPrice || p.price <= filters.value.maxPrice) &&
      (!filters.value.beds || p.beds >= filters.value.beds) &&
      (!filters.value.internet || p.internet === filters.value.internet)
    )
  })
})
</script>

